et-Content -Path $reporterPyPath -Value $reporterPyContent -Encoding UTF8 Write-Host "Arquivo criado: $reporterPyPath"  # Cria o arquivo templates.json $templatesJsonPath = Join-Path $errorReportingDir "templates.json" Set-Content -Path $templatesJsonPath -Value $templatesJsonContent -Encoding UTF8 Write-Host "Arquivo criado: $templatesJsonPath"  # Cria o arquivo error_queue.json $errorQueueJsonPath = Join-Path $errorReportingDir "error_queue.json" Set-Content -Path $errorQueueJsonPath -Value $errorQueueJsonContent -Encoding UTF8 Write-Host "Arquivo criado: $errorQueueJsonPath"
