#>## setup_error_reporting.ps1 ## caminho C:\4All_OS\privado\scrripts ## script PowerShell que cria a estrutura de diret√≥rios e arquivos para o sistema de relat√≥rios de erros colaborativo. Cada arquivo gerado inclui instru√ß√µes no in√≠cio, especificando o nome do arquivo, o caminho e orienta√ß√µes para os desenvolvedores.   # Caminho base do projeto $basePath = "C:\4All_OS\modules\4All_Comms"  # Diret√≥rio do m√≥dulo de relat√≥rios de erros $errorReportingDir = Join-Path $basePath "error_reporting"  # Verifica e cria o diret√≥rio se n√£o existir if (!(Test-Path -Path $errorReportingDir)) {     New-Item -Path $errorReportingDir -ItemType Directory -Force     Write-Host "Diret√≥rio criado: $errorReportingDir" } else {     Write-Host "Diret√≥rio j√° existe: $errorReportingDir" }  # Conte√∫do do arquivo reporter.py $reporterPyContent = @" # Arquivo: reporter.py # Caminho: C:\4All_OS\modules\4All_Comms\error_reporting\reporter.py # Instru√ß√µes: Este m√≥dulo coleta e envia relat√≥rios de erros. Ele armazena localmente quando offline e envia via P2P quando online.  import json import os from datetime import datetime from local_storage import save_local from p2p_node import send_to_peer from encryption import encrypt  ERROR_QUEUE_FILE = os.path.join(os.path.dirname(__file__), 'error_queue.json')  def now():     return datetime.now().strftime('%Y-%m-%d %H:%M:%S')  def is_online():     # Implementar verifica√ß√£o de conectividade     return False  # Placeholder  def report_error(user_id, description, module_name="unknown", suggestion=None):     log = {         "user": user_id,         "module": module_name,         "description": description,         "suggestion": suggestion,         "timestamp": now()     }      encrypted_log = encrypt(log)      if not is_online():         save_local(ERROR_QUEUE_FILE, encrypted_log)     else:         send_to_peer("owner_id", encrypted_log) "@  # Conte√∫do do arquivo templates.json $templatesJsonContent = @" {     "default": "Descreva o erro encontrado.",     "suggestion": "Deseja sugerir uma corre√ß√£o?" } "@  # Conte√∫do do arquivo error_queue.json $errorQueueJsonContent = @" [] "@  # Cria o arquivo reporter.py $reporterPyPath = Join-Path $errorReportingDir "reporter.py" Set-Content -Path $reporterPyPath -Value $reporterPyContent -Encoding UTF8 Write-Host "Arquivo criado: $reporterPyPath"  # Cria o arquivo templates.json $templatesJsonPath = Join-Path $errorReportingDir "templates.json" Set-Content -Path $templatesJsonPath -Value $templatesJsonContent -Encoding UTF8 Write-Host "Arquivo criado: $templatesJsonPath"  # Cria o arquivo error_queue.json $errorQueueJsonPath = Join-Path $errorReportingDir "error_queue.json" Set-Content -Path $errorQueueJsonPath -Value $errorQueueJsonContent -Encoding UTF8 Write-Host "Arquivo criado: $errorQueueJsonPath"
